record.methods:
PROCEDURE Init
*Init
If not opendbf ('Docum','Tiponum')or;
	not opendbf ('Provedor','Numprov')or;
	not opendbf ('Almacen','Numalm')
	Return .F.
Endif

WITH THISFORM

	* Variables
	.leefecha1.Setvalue(date(year(date()),month(date()),1))
	.leefecha2.Setvalue(Date())

	.Opciones.opcion3.value = 1

	.Usuario.value = Numuser + '  ' + Nomuser
	*Cursor
	Create Cursor Tempg (;
	    Numdoc	 	C(10),;
	    Fecha		D(8),;
		Numprov 	C(5),;
		Nomprov  	C(60),;
		Subtot		N(12,2),;
		Saldo		N(12,4),;
		Autoriza 	L(1),;
		ClavAut		C(5),;
		Yamodi		L(1),;
		Autoriza2 	L(1),;
		ClavAut2	C(5),;
		Yamodi2		L(1),;
		Comprador	C(50),;
		Referen		C(50),;
		OC			C(50),;
		Nomalm		C(30))

	*Cosultar
	nOpcion = .Opciones.value

	DO CASE

		CASE nOpcion = 1
			.ConsDepto()

		CASE nOpcion = 2
			.Conscomp()

		CASE nOpcion = 3
			.Consultar()

	ENDCASE

	* Muestra los datos de tempoca
	.LockScreen = .T.
	With ThisForm.Grid1
		.RecordSourceType = 1
		.RecordSource = 'Tempg'
		.Column1.ControlSource  = 'Mexdate2(Tempg.Fecha)'
		.Column2.ControlSource  = 'Tempg.Numdoc'
		.Column3.ControlSource  = 'Tempg.Numprov'
		.Column4.ControlSource  = 'Allt(Tempg.Nomprov)'
		.Column7.ControlSource  = 'Tempg.Subtot'
		.Column6.ControlSource  = 'Tempg.Autoriza'
		.Column8.ControlSource  = 'Tempg.ClavAut'
		.Column5.ControlSource  = 'Tempg.Saldo'
		.Column9.ControlSource  = 'Tempg.Autoriza2'
		.Column10.ControlSource  = 'Tempg.ClavAut2'
		.Column11.ControlSource  = 'Tempg.Comprador'
		.Column12.ControlSource  = 'Tempg.Referen'
		.Column13.ControlSource  = 'Tempg.OC'
		.Column14.ControlSource  = 'Tempg.Nomalm'
		.Setfocus
	Endwit
	.LockScreen = .f.

	* Colorear renglón activo.
	Thisform.Activerecno=0
	.SetAll('DynamicBackColor', 'iif(Recno("Tempg")==ThisForm.ActiveRecno, RGB(255,255,200), RGB(255,255,255))', 'Column')


	* Muestra mensaje si no se encontro soporte
	If Reccount('Tempg')==0
		Alerta('No se encontró informacion')
		Return
	Endif

	.Command1.visible = .F.
	.Command3.visible = .F.
	.Grid1.Column6.Enabled = .F.
	.Grid1.Column9.Enabled = .F.

	*Ocultar botones
	iF Numuser == '   WS' or Numuser == ' VTSG' or Numuser == 'ADCEO' or Numuser == 'ADMOP' or Numuser == 'GCOMP' or Numuser == 'VTSP1' or Numuser == 'VTSP2' or Numuser == 'VTSP3'

		.Command1.visible = .T.
		.Grid1.Column6.Enabled = .T.

	Else

		.Command1.visible = .F.
		.Grid1.Column6.Enabled = .F.

	Endif

	*Ocultar botones
	iF Numuser == '   WS' or Numuser == ' ADMF' or Numuser == 'ADCEO'

		.Command3.visible = .T.
		.Grid1.Column9.Enabled = .T.

	Else

		.Command3.visible = .F.
		.Grid1.Column9.Enabled = .F.

	Endif

EndWith



ENDPROC
PROCEDURE conscomp
*Consultar

WITH THISFORM

	*Borrar Tabla
	Sele Tempg
	zap

	*Variables
	cNumprov 	= .Leeprov1.Numprov.value
	vFecha1		= .LeeFecha1.Value
	vFecha2		= .LeeFecha2.Value
	cNumAlm		= .LeeAlm1.Numalm.value

	csql =  '  Select Docum.Fecha, Docum.Tipodoc, Docum.Numdoc, NVL(Docum.Numprov,"") AS Numprov, Docum.Numalm, Docum.DatosExtra, ' + ;
	              ' Docum.Importe, Docum.Impuesto1, Docum.Impuesto2, Docum.Descuento, Docum.Autoriza, ' + ;
	              ' NVL(Provedor.Nomprov,"") AS Nomprov, Docum.ClavAut, NVL(Provedor.Saldo,0) AS Saldo, Docum.Autoriza2, Docum.ClavAut2 ' + ;
	        '  From Docum ' + ;
	            ' Left Join Provedor on Docum.Numprov = Provedor.Numprov ' + ;
	          '  Where Between(Docum.Fecha , m.Val2, m.Val3) and ' +;
	          		 ' Docum.Tipodoc  == "RQ" and ' +;
	          		 ' Docum.Autoriza2 = .F.   and ' +;
	          		 ' Docum.Status <> 1   and ' +;
	     iIF(Empty(cNumprov),".T.", "Docum.Numprov = m.Val1 ") + ' And '+;
	     iIF(Empty(cNumAlm),".T.", "Docum.NumAlm = m.Val4 ") +;
	          		 ' Order by Docum.Numdoc '


	if not sqlmsl( 'TempPed', csql, cNumprov, vFecha1, vFecha2, cNumalm )
		return
	endif


	Sele TempPed
	Scan

		Sele Almacen
		=Seek(Tempped.Numalm,'Almacen','Numalm')
		iF Found()
			Sele Almacen
				cNomalm = Almacen.Nomalm
		Else
			cNomalm = ""
		Endif

		*Pasar datos a Tempg
		Sele Tempg
			Append Blank
				Replace	Fecha		with	TempPed.Fecha,;
						Numdoc		with	TempPed.Numdoc,;
						Numprov		with	TempPed.Numprov,;
						Nomprov		with	TempPed.Nomprov,;
						Subtot		with	TempPed.Importe - TempPed.Descuento + TempPed.Impuesto1 + TempPed.Impuesto2,;
						Autoriza 	with	TempPed.Autoriza,;
						ClavAut		with	Allt(TempPed.ClavAut),;
						Yamodi		with	iIF(TempPed.Autoriza=.T.,.T.,.F.),;
						Autoriza2 	with	TempPed.Autoriza2,;
						ClavAut2	with	TempPed.ClavAut2,;
						Yamodi2		with	iIF(TempPed.Autoriza2=.T.,.T.,.F.),;
						Saldo		with	TempPed.Saldo,;
						Comprador	with    Allt(ValProp(TempPed.datosextra, "Comprador")),;
						Referen		with	Allt(ValProp(TempPed.datosextra, "Referencia")),;
						OC			with	Allt(ValProp(TempPed.datosextra, "Documento")),;
						Nomalm		with	cNomalm

	  Sele TempPed

	Endscan

	Sele Tempg
	GO TOP

	*Actualizar Grid
	.Grid1.Refresh()

ENDWITH


ENDPROC
PROCEDURE consdepto
*Consultar

WITH THISFORM

	*Borrar Tabla
	Sele Tempg
	zap

	*Variables
	cNumprov 	= .Leeprov1.Numprov.value
	vFecha1		= .LeeFecha1.Value
	vFecha2		= .LeeFecha2.Value
	cNumAlm		= .LeeAlm1.Numalm.value

	csql =  '  Select Docum.Fecha, Docum.Tipodoc, Docum.Numdoc, Docum.Numprov, Docum.Numalm, Docum.DatosExtra, ' + ;
	              ' Docum.Importe, Docum.Impuesto1, Docum.Impuesto2, Docum.Descuento, Docum.Autoriza, ' + ;
	              ' Provedor.Nomprov, Docum.ClavAut, Provedor.Saldo, Docum.Autoriza2, Docum.ClavAut2 ' + ;
	        '  From Docum ' + ;
	            ' Left Join Provedor on Docum.Numprov = Provedor.Numprov ' + ;
	          '  Where Between(Docum.Fecha , m.Val2, m.Val3) and ' +;
	          		 ' Docum.Tipodoc  == "RQ" and ' +;
	          		 ' Docum.Autoriza = .F.   and ' +;
	          		 ' Docum.Status <> 1   and ' +;
	     iIF(Empty(cNumprov),".T.", "Docum.Numprov = m.Val1 ") + ' And '+;
	     iIF(Empty(cNumAlm),".T.", "Docum.NumAlm = m.Val4 ") +;
	          		 ' Order by Docum.Numdoc '


	if not sqlmsl( 'TempPed', csql, cNumprov, vFecha1, vFecha2, cNumalm )
		return
	endif


	Sele TempPed
	Scan

		Sele Almacen
		=Seek(Tempped.Numalm,'Almacen','Numalm')
		iF Found()
			Sele Almacen
				cNomalm = Almacen.Nomalm
		Else
			cNomalm = ""
		Endif

		*Pasar datos a Tempg
		Sele Tempg
			Append Blank
				Replace	Fecha		with	TempPed.Fecha,;
						Numdoc		with	TempPed.Numdoc,;
						Numprov		with	TempPed.Numprov,;
						Nomprov		with	TempPed.Nomprov,;
						Subtot		with	TempPed.Importe - TempPed.Descuento,;
						Autoriza 	with	TempPed.Autoriza,;
						ClavAut		with	TempPed.ClavAut,;
						Yamodi		with	iIF(TempPed.Autoriza=.T.,.T.,.F.),;
						Autoriza2 	with	TempPed.Autoriza2,;
						ClavAut2	with	TempPed.ClavAut2,;
						Yamodi2		with	iIF(TempPed.Autoriza2=.T.,.T.,.F.),;
						Saldo		with	TempPed.Saldo,;
						Comprador	with    Allt(ValProp(TempPed.datosextra, "Comprador")),;
						Referen		with	Allt(ValProp(TempPed.datosextra, "Referencia")),;
						OC			with	Allt(ValProp(TempPed.datosextra, "Documento")),;
						Nomalm		with	cNomalm

	  Sele TempPed

	Endscan

	Sele Tempg
	GO TOP

	*Actualizar Grid
	.Grid1.Refresh()

ENDWITH


ENDPROC
