PROCEDURE Init
* INIT

LParameter cNumcli

With Thisform

	.pageFrame1.Page1.Leecli1.Numcli.value = cNumcli

Endwith


* 1. Abrir Tablas
If  not opendbf ('CXC','keyCXC') or ;
	not opendbf ('DOCUM','Tiponum')or ;
	not opendbf ('Cia') or ;
	not opendbf ('Clientes','Numcli') or ;
	not opendbf ('SucCli','NumCliSuc') or ;
	not opendbf ('Reportes')
	return .F.
endif

* 2 Definir Valores Iniciales
With Thisform
	*.vFecha1.Value = (date(year(date()),month(date()),1))
	.vFecha1.Value = { // }
	.vFecha2.Value = Date()

	*.vFecha1.setvalue(date(year(date()),month(date()),1))
	.vFecha1.setvalue({ // })

	.vFecha2.setvalue(Date())	

* Cursor para grafica Continer1
	create cursor Sales(;
		amount N(12,2),;
		cName c(10))

* Cursor para grafica Continer2
	create cursor Sales2(;
		amount N(12,2),;
		cName c(10))
* Cursor para grafica Continer2
	create cursor Sales3(;
		amount N(12,2),;
		cName c(10))

* Cursor para grafica Continer2
	create cursor Sales4(;
		amount N(12,2),;
		cName c(10))


*	Sele Sales
*	appen blank

* 3. Crear Cursor. De Temp1
	Create Cursor Temp1(;
		numcli     c(5),;
		Nomcli     c(30),;
		FechaDoc   d(8),;
		FVenc      d(8),;
		Numdoc     c(10),;
		Importe    N(12,2),;
		Divisa     C(1),;
		NoVenc     N(12,2),;
		venc15     N(12,2),;
		Venc30     N(12,2),;
		Venc45     N(12,2),;
		Venc60     N(12,2),;
		Venc60Mas  N(12,2),;
		Saldos	   N(12,2),;
		Conc	   c(2),;		
		KeyDocum   C(12),;
		KeyRefer   c(12),;
		TC		   N(8,4))

* 4. Crear Cursor de Resumen
	Create Cursor Temp2(;
		numcli c(5),;
		Nomcli c(30),;
		FechaDoc d(8),;
		FVenc    d(8),;
		Numdoc   c(10),;
		Importe  N(12,2),;
		Divisa   C(1),;
		NoVenc     N(12,2),;
		venc15     N(12,2),;
		Venc30     N(12,2),;
		Venc45     N(12,2),;
		Venc60     N(12,2),;
		Venc60Mas  N(12,2),;
		Saldos	   N(12,2))




* 5. Crear Cursor. De Temp3 De Pronóstico de Cobranza
	Create Cursor Temp3 (;
		numcli 	   c(5),;
		Nomcli     c(30),;
		FechaDoc   d(8),;
		FVenc      d(8),;
		Numdoc     c(10),;
		Importe    N(12,2),;
		Divisa     C(1),;
		Vencidas   N(12,2),;
		Pron15     N(12,2),;
		Pron30     N(12,2),;
		Pron45     N(12,2),;
		Pron60     N(12,2),;
		Pron60Mas  N(12,2),;
		Conc	   c(2),;		
		KeyDocum   C(12),;
		KeyRefer   c(12),;
		Saldos	   N(12,2),;
		TC		   N(8,4))

* 6. Crear Cursor de Resumen de Pronóstico de Cobranza
		Create Cursor Temp4(;
		numcli	    c(5),;
		Nomcli      c(30),;
		FechaDoc    d(8),;
		FVenc       d(8),;
		Numdoc      c(10),;
		Importe     N(12,2),;
		Divisa      C(1),;
		Vencidas     N(12,2),;
		Pron15     N(12,2),;
		Pron30     N(12,2),;
		Pron45     N(12,2),;
		Pron60     N(12,2),;
		Pron60Mas  N(12,2),;
		Saldos	   N(12,2))



*Colocar valoes de Temp1 en la GRID1

	.LockScreen = .t.
	 With ThisForm.pageFrame1.Page1.Grid1
	  .RecordSourceType = 1
	  .RecordSource = 'temp1'

	  .Column1.controlSource = 'temp1.Numcli'
	  .Column2.ControlSource = 'temp1.Nomcli'
	  .Column3.ControlSource = 'mexdate2(temp1.Fechadoc)'
	  .Column4.ControlSource = 'mexdate2(temp1.Fvenc)'
	  .Column5.ControlSource = 'temp1.conc'
	  .Column6.ControlSource = 'temp1.Numdoc'
	  .Column7.ControlSource = 'temp1.Importe'
	  .Column8.ControlSource = 'temp1.divisa'
	  .Column9.ControlSource = 'temp1.Novenc'
	  .Column10.ControlSource = 'temp1.Venc15'
	  .Column11.ControlSource = 'temp1.Venc30'
	  .Column12.ControlSource = 'temp1.Venc45'
	  .Column13.ControlSource = 'temp1.Venc60'
	  .Column14.ControlSource = 'temp1.Venc60mas'
	  .Column15.ControlSource = 'temp1.Saldos'

	 EndWith
