	  .Column11.ControlSource = 'temp4.Pron30'
	  .Column12.ControlSource = 'temp4.Pron45'
	  .Column13.ControlSource = 'temp4.Pron60'
	  .Column14.ControlSource = 'temp4.Pron60mas'
	  .Column15.ControlSource = 'temp4.Saldos'

	 EndWith
	 .LockScreen = .f.

	 * Consultar
	 .consultar1()


EndWith
ENDPROC
PROCEDURE consulta2
* consulta 2
*Generar totales e indicadores de cobranza

* 1. sumar Columas a Variables
* 2. Generar un Renglon con totales
* 3. Generar un Renglon con Porcentjes
* 4. Generar un Renglon con Indicadores


*Limpiar
sele temp2
zap
.pageFrame1.Page1.Grid2.refresh

* 1 sumar a Variables
	Selec temp1
	Sum (temp1.Importe) for temp1.divisa = 'P' to nImporteP
	Sum (temp1.Importe) for temp1.divisa = 'D' to nImporteD
	Sum (temp1.NoVenc)  for temp1.divisa = 'P' to nNoVencP
	Sum (temp1.NoVenc)	for temp1.divisa = 'D' to nNovencD
	Sum (temp1.Venc15)	for temp1.divisa = 'P' to nVenc15P
	Sum (temp1.Venc15)	for temp1.divisa = 'D' to nVenc15D
	Sum (temp1.Venc30)	for temp1.divisa = 'P' to nVenc30P
	Sum (temp1.Venc30)	for temp1.divisa = 'D' to nVenc30D
	Sum (temp1.Venc45)	for temp1.divisa = 'P' to nVenc45P
	Sum (temp1.Venc45)	for temp1.divisa = 'D' to nVenc45D
	Sum (temp1.Venc60)  for temp1.divisa = 'P' to nVenc60P
	Sum (temp1.Venc60)  for temp1.divisa = 'D' to nVenc60D
	Sum (temp1.Venc60mas) for temp1.divisa = 'P' to nVenc60masP
	Sum (temp1.Venc60mas) for temp1.divisa = 'D' to nVenc60masD
	Sum (temp1.Saldos)	for temp1.divisa = 'P' to nSaldosP
	Sum (temp1.Saldos)	for temp1.divisa = 'D' to nSaldosD
	Sum (temp1.Saldos)	for temp1.divisa = 'P' to nSaldoTotalP
	Sum (temp1.Saldos)	for temp1.divisa = 'D' to nSaldoTotalD

* 2. Renglon de Totales En Pesos

Sele temp2
	Appen Blank
		Replace Temp2.Importe with nImporteP,;
				Temp2.divisa with 'P',;
				Temp2.NoVenc with nNoVencP,;
				Temp2.Venc15 with nVenc15P,;
				Temp2.Venc30 with nVenc30P,;
				Temp2.Venc45 with nVenc45P,;
				Temp2.Venc60 with nVenc60P,;
				Temp2.Venc60mas with nVenc60masP,;
				Temp2.Saldos with nSaldototalP

* 2.1 Renglon de Totales En Dolares

	Sele temp2
	Appen Blank
		Replace Temp2.Importe with nImporteD,;
				Temp2.divisa with 'D',;
				Temp2.NoVenc with nNoVencD,;
				Temp2.Venc15 with nVenc15D,;
				Temp2.Venc30 with nVenc30D,;
				Temp2.Venc45 with nVenc45D,;
				Temp2.Venc60 with nVenc60D,;
				Temp2.Venc60mas with nVenc60masD,;
				Temp2.Saldos with nSaldototalD


* 3. * Calculo de porcentajes
	* El total es nSaldoTotalP para pesos
	*y nSaldoTotalD para Dolares.		
	* Calculamos el porcentaje de cada columna

	Sele Temp2
	Appen Blank
		Replace temp2.NoVenc with (nNoVencP +(nNoVencD * Cia.Tccredito)) / (nSaldoTotalP+(nSaldototalD * Cia.Tccredito)) * 100,;
				temp2.Venc15 with (nVenc15P +(nVenc15D * Cia.Tccredito)) / (nSaldoTotalP+(nSaldototalD * Cia.Tccredito)) * 100,;
				temp2.Venc30 with (nVenc30P +(nVenc30D * Cia.Tccredito)) / (nSaldoTotalP+(nSaldototalD * Cia.Tccredito)) * 100,;
				temp2.Venc45 with (nVenc45P +(nVenc45D * Cia.Tccredito)) / (nSaldoTotalP+(nSaldototalD * Cia.Tccredito)) * 100,;
				temp2.Venc60 with (nVenc60P +(nVenc60D * Cia.Tccredito)) / (nSaldoTotalP+(nSaldototalD * Cia.Tccredito)) * 100,;
				temp2.Venc60mas with (nVenc60masP +(nVenc60masD * Cia.Tccredito)) / (nSaldoTotalP+(nSaldototalD * Cia.Tccredito)) * 100,;
				temp2.Saldos with temp2.Novenc + temp2.Venc15 + temp2.Venc30 + temp2.Venc45 + temp2.Venc60 + temp2.Venc60mas


* 4. Registrar indicadores.

	Sele Temp2
	Appen Blank
		Replace temp2.NoVenc with 85,;
				temp2.venc15 with 10,;
				Temp2.venc30 with 2,;
				temp2.venc45 with 1,;
				temp2.venc60 with 1,;
				temp2.venc60mas with 1,;
				Temp2.saldos with  temp2.NoVenc + temp2.venc15 + Temp2.venc30 + temp2.venc45 + temp2.venc60 + temp2.venc60mas


*Resfrescar GRID1
GO TOP
	   .pageFrame1.Page1.Grid2.Refresh


*Preparar cursor para actualizar la grafica
*Grafica en Pesos

Sele Sales
ZAP

sele SALES
	appen blan
			replace Cname 	with 'No Venc',;
					AMOUNT 	with nNoVencP	
	appen blan
			replace cName	with 'Venc 15',;
					amount	with nVenc15p
	appen blan
			replace cName	with 'Venc 30',;
					amount	with nVenc30P
	appen blan
			replace cName	with 'Venc 45',;
					amount	with nVenc45P
	appen blan
			replace cName	with 'Venc 60',;
					amount	with nVenc60P
	appen blan
			replace cName	with 'Venc 60+',;
					amount	with nVenc60masP

SELECT (Thisform.pageFrame1.Page1.Container1.Tag)
Thisform.pageFrame1.Page1.Container1.Refresh


*Preparar cursor para actualizar la grafica
*Grafica en Pesos

Sele Sales2
ZAP

sele SALES2
	appen blan
			replace Cname 	with 'No Venc',;
					AMOUNT 	with nNoVencD	
	appen blan
			replace cName	with 'Venc 15',;
					amount	with nVenc15D
	appen blan
			replace cName	with 'Venc 30',;
					amount	with nVenc30D
	appen blan
			replace cName	with 'Venc 45',;
					amount	with nVenc45D
	appen blan
			replace cName	with 'Venc 60',;
					amount	with nVenc60D
	appen blan
			replace cName	with 'Venc 60+',;
					amount	with nVenc60masD

SELECT (Thisform.pageFrame1.Page1.Container2.Tag)
Thisform.pageFrame1.Page1.Container2.Refresh

ENDPROC
PROCEDURE consulta3

*Limpiar GRID4
	Sele temp3
	ZAP
	.pageFrame1.Page2.grid3.refresh

Sele TempSaldo
	Scan

		*Buscar Nombre del Cliente
	    Sele clientes
		=SEEK (tempsaldo.numcli,'Clientes','Numcli')
			cNomCli = clientes.nomcli


		Sele temp3
			Appen Blank
				Replace numcli  with TempSaldo.numcli,;
						NomCli  with cNomCli,;
						Conc	with TempSaldo.Conc,;
						Numdoc  with subStr(TempSaldo.Refer,3,10),;
						importe with TempSaldo.importe,;
						Saldos  with TempSaldo.Saldo,;
						divisa	with TempSaldo.Divisa,;
						fechaDoc with TempSaldo.Fecha,;
						Fvenc   with TempSaldo.Venc,;
						tc 		with tempsaldo.tc


		* Se Hacen casos para Filtrar columnas.
		Do Case


			 &&	* No Vencido   La Fecha del vencimiento es menor que la fecha actual
			Case  tempsaldo.Venc <= vFecha2
				Replace Temp3.vencidas with TempSaldo.saldo


			Case BETWEEN(TEMPSALDO.VENC,vFecha2+1,vFecha2+15)
				Replace Temp3.Pron15 with TempSaldo.saldo

			Case BETWEEN(TEMPSALDO.VENC,vFecha2+16,vFecha2+30)
				Replace Temp3.Pron30 with TempSaldo.saldo

			Case BETWEEN(TEMPSALDO.VENC,vFecha2+31,vFecha2+45)
				Replace Temp3.Pron45 with TempSaldo.saldo

			Case BETWEEN(TEMPSALDO.VENC,vFecha2+46,vFecha2+60)
				Replace Temp3.Pron60 with TempSaldo.saldo

			Case TEMPSALDO.VENC >= vFecha2+61
				Replace Temp3.Pron60mas with TempSaldo.saldo


		EndCase 		


		sele tempsaldo
	EndScan

*Refresh GRID3

.pageFrame1.Page2.GRID3.REFRESH

ENDPROC
