	Sum (temp3.pron15)	for temp3.divisa = 'P' to nPron15P
	Sum (temp3.pron15)	for temp3.divisa = 'D' to nPron15D
	Sum (temp3.pron30)	for temp3.divisa = 'P' to nPron30P
	Sum (temp3.pron30)	for temp3.divisa = 'D' to nPron30D
	Sum (temp3.pron45)	for temp3.divisa = 'P' to nPron45P
	Sum (temp3.pron45)	for temp3.divisa = 'D' to nPron45D
	Sum (temp3.pron60)  for temp3.divisa = 'P' to nPron60P
	Sum (temp3.pron60)  for temp3.divisa = 'D' to nPron60D
	Sum (temp3.pron60mas) for temp3.divisa = 'P' to nPron60masP
	Sum (temp3.pron60mas) for temp3.divisa = 'D' to nPron60masD
	Sum (temp3.Saldos)	for temp3.divisa = 'P' to nSaldosP
	Sum (temp3.Saldos)	for temp3.divisa = 'D' to nSaldosD
	Sum (temp3.Saldos)	for temp3.divisa = 'P' to nSaldoTotalP
	Sum (temp3.Saldos)	for temp3.divisa = 'D' to nSaldoTotalD
	Sum (temp3.Importe) to nImporte
	Sum (temp3.Vencidas)	to nVencidas
	Sum (temp3.Pron15)	to nPron15
	Sum (temp3.Pron30)	to nPron30
	Sum (temp3.Pron45)	to nPron45
	Sum (temp3.Pron60)  to nPron60
	Sum (temp3.Pron60mas) to nPron60mas
