				Temp4.Pron15 with nPron15D,;
				Temp4.Pron30 with nPron30D,;
				Temp4.Pron45 with nPron45D,;
				Temp4.Pron60 with nPron60D,;
				Temp4.Pron60mas with nPron60masD,;
