PROCEDURE consultar1
*consultar1

* Consultar
* Iniciarlizar variables
With ThisForm
	vFecha1 = .vfecha1.dValue
	vFecha2 = .vfecha2.dValue
	cNumcli = .pageFrame1.Page1.Leecli1.Numcli.value

	*Limpiar GRID1
	Sele temp1
	ZAP
	.pageFrame1.Page1.grid1.refresh

	* Generar Consulta  Abonos.
	*Sele * from cxc where cxc.ca = '1' and;
	*	between (cxc.Fecha, vFecha1,vfecha2) into cursor temp

	*	Saldos Vencidos
	* Left Join clientes on Cxc.Numcli = Clientes.numcli ;

	csql =  '  Select * From Cxc ' + ;
		 	     		 '  Where ' + ;
	        	        	  '  between (cxc.Fecha, m.Val1, m.Val2)   and ' + ;
	        	        	  '  Cxc.saldo  >  0        and ' + ;
	        	        	  '  Cxc.Numcli = m.Val3    and ' + ;
	        	        	  '  Cxc.Ca     = "0"           ' + ;
	        	        	   'Order By Cxc.numcli, Cxc.Divisa, Cxc.Venc '

				if not sqlmsl( 'Tempsaldo', csql, vFecha1, vfecha2, cNumcli  )
					return
				Endif




*	Sele * from cxc where cxc.Ca = '0' and ;
*	cxc.saldo > 0 and;
*	between (cxc.Fecha, vFecha1,vfecha2)  ;
*	order by numcli,divisa,cxc.Venc;
*	into cursor tempSaldo
*	
*	Sele tempSaldo

	* La fecha de Referencia es la 2.


	* Pasar Datos de consulta a Cursor
	Sele TempSaldo
	Scan


		*Buscar Nombre del Cliente
	    Sele clientes
		=SEEK (tempsaldo.numcli,'Clientes','Numcli')
			cNomCli = clientes.nomcli


		Sele temp1
			Appen Blank
				Replace numcli  with TempSaldo.numcli,;
						NomCli  with cNomCli,;
						Conc	with TempSaldo.Conc,;
						Numdoc  with subStr(TempSaldo.Refer,3,10),;
						importe with TempSaldo.importe,;
						Saldos  with TempSaldo.Saldo,;
						divisa	with TempSaldo.Divisa,;
						fechaDoc with TempSaldo.Fecha,;
						Fvenc   with TempSaldo.Venc,;
						tc 		with tempsaldo.tc


		* Se Hacen casos para Filtrar columnas.
		Do Case


			 &&	* No Vencido   La Fecha del vencimiento es menor que la fecha actual
			Case  Tempsaldo.Venc >= vFecha2 or Empty(Tempsaldo.Venc)
				Replace Temp1.NoVenc with TempSaldo.saldo


			Case BETWEEN(TEMPSALDO.VENC,vFecha2-15,vFecha2-1)
				Replace Temp1.Venc15 with TempSaldo.saldo

			Case BETWEEN(TEMPSALDO.VENC,vFecha2-30,vFecha2-16)
				Replace Temp1.Venc30 with TempSaldo.saldo

			Case BETWEEN(TEMPSALDO.VENC,vFecha2-45,vFecha2-31)
				Replace Temp1.Venc45 with TempSaldo.saldo

			Case BETWEEN(TEMPSALDO.VENC,vFecha2-60,vFecha2-46)
				Replace Temp1.Venc60 with TempSaldo.saldo

			Case TEMPSALDO.VENC<= vFecha2-61
				Replace Temp1.Venc60mas with TempSaldo.saldo


		EndCase 		


		sele tempsaldo
	EndScan


	Sele temp1

	*LLenar GRID2
	.consulta2()

	*LLenar GRID3
	.consulta3()

	*LLenar GRID4
	.consulta4()

	*Resfrescar GRID1
	 .pageFrame1.Page1.Grid1.Refresh

EndWith 	







ENDPROC
